use crate::api::types::*;
use crate::*;
use sbor::rust::fmt::Debug;
use transaction_data::*;

#[derive(
    Debug,
    Clone,
    Eq,
    PartialEq,
    ScryptoCategorize,
    ScryptoEncode,
    ScryptoDecode,
    ManifestCategorize,
    ManifestEncode,
    ManifestDecode,
)]
pub struct MetadataSetInvocation {
    pub receiver: RENodeId,
    pub key: String,
    pub value: String,
}

impl Invocation for MetadataSetInvocation {
    type Output = ();

    fn fn_identifier(&self) -> FnIdentifier {
        FnIdentifier::Native(NativeFn::Metadata(MetadataFn::Set))
    }
}

impl SerializableInvocation for MetadataSetInvocation {
    type ScryptoOutput = ();

    fn native_fn() -> NativeFn {
        NativeFn::Metadata(MetadataFn::Set)
    }
}

impl Into<CallTableInvocation> for MetadataSetInvocation {
    fn into(self) -> CallTableInvocation {
        NativeInvocation::Metadata(MetadataInvocation::Set(self)).into()
    }
}

